    private void txtAnoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtAnoKeyTyped
        char c= evt.getKeyChar();
        if ((c<'0') || (c>'9'))
        {evt.consume();}
    }//GEN-LAST:event_txtAnoKeyTyped

